function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var start = g_form.getValue('start_date');

    //Type appropriate comment here, and begin script below
    var ga = new GlideAjax('lmsAjax');
    ga.addParam('sysparm_name', 'getDatediff');
    ga.addParam('sysparm_start', start);
    ga.addParam('sysparm_end', newValue);
    ga.getXML(getDuration);

    // Define the getDuration function outside of onChange
    function getDuration(response) {
        var answer = response.responseXML.documentElement.getAttribute("answer");
//         if (answer.isShortDuration) {
//             alert('Duration is less than 500 milliseconds!');
//         }
        if (answer && answer !== '') {
            // If answer is not empty, proceed with further logic
            var days = (answer / (1000 * 60 * 60 * 24));
			var days1 =days+1;
            g_form.setValue('duration', days1);
        } else {
            // If answer is empty or null, handle it accordingly

            alert('Empty or null response received.');
        }

        // Display the XML response in the console

        // Convert XML response to string and display in alert
//         var xmlString = new XMLSerializer().serializeToString(response.responseXML);
// alert('XML Response:\n' + xmlString);
// alert('Response status:', response.status);
// alert('Response text:', response.responseText);
    }
}
